INSERT INTO `blog_collect_draft` VALUES (25, 1, '封面播放器', '<img src=\"/storage/topic/20260223/5dc43656d49fc40d3f860d94ddd00a05.png\" class=\"thumbile\" alt=\"封面播放器\">\r\n                        <img src=\"/img/10.png\" class=\"thumbile\" alt=\"ie兼容10\">\r\n                         <div class=\"row text-center ggpdz\" style=\"margin-bottom:30px;margin-top: 10px;height: 90px;background-image: url(img/myinfobg.png)\">                    \r\n                            <script async src=\"//pagead2.googlesyndication.com/pagead/js/adsbygoogle.js\"></script>\r\n                            <!-- a820x90 -->\r\n                            <ins class=\"adsbygoogle\" style=\"display:inline-block;width:820px;height:90px\" data-ad-client=\"ca-pub-2935286260943234\" data-ad-slot=\"1551313993\"></ins>\r\n                            <script>\r\n                                (adsbygoogle = window.adsbygoogle || []).push({});\r\n                            </script>\r\n                        </div>\r\n\r\n                        <div class=\"thumbile\">\r\n                            <a href=\"https://www.jq22.com/yanshi3467\" target=\"_blank\" class=\"btn btn-success\"><i class=\"fa fa-eye\"></i>  查看演示</a>\r\n                            <a href=\"\" target=\"_blank\" class=\"btn btn-info\" style=\"display:none\"><i class=\"fa fa-globe\"></i>  website</a>\r\n                            <a onclick=\"mydown()\" class=\"btn btn-danger btn-danet\" data-toggle=\"modal\" data-target=\"#mydown\"><i class=\"fa fa-cloud-download\"></i>  立即下载</a>  \r\n                            <a class=\"btn jcjg\" style=\"line-height: 28px;\"><img src=\"img/jcjg.png\"></a>                          \r\n                        </div>\r\n\r\n                       <div class=\"alert cjms\" role=\"alert\">插件描述：之前蓝色音乐播放器插入音乐播放器很麻烦，我做了一个封面炫酷的链接音乐播放器</div>                    \r\n                        <p><strong>更新时间：2018-12-15 22:42:20</strong></p>\n<p><strong>更新说明：</strong>解决歌曲与图片对不上的问题</p>\n<hr>\n<p style=\"white-space: normal;\"><strong>更新时间：2018-12-12 16:54:11</strong><br></p>\n<p><strong>更新说明：</strong>歌曲图片存储问题。</p>\n<p>1.七牛云可以自己免费赠送10G存储，足够大家使用</p>\n<p>2.我的歌曲图片是存储在MXLOGO官网。不用担心保留问题</p>\n<p>3.如果你没有域名，可以直接用4VTK的歌曲。</p>\n<hr>\n<p style=\"white-space: normal;\"><strong>更新时间：2018-12-8 01:06:07</strong><br></p>\n<p style=\"white-space: normal;\"><strong>更新说明：</strong></p>\n<p>这是封面音乐播放器2018年最后一次更新。</p>\n<p>1.已经可以响应式，支持移动端播放。</p>\n<p>2.和上次一样，解决音乐存储问题。小公司或个人服务器太小。不能存储过多音乐问题</p>\n<hr>\n<p style=\"white-space: normal;\"><strong>更新时间：Mon May 29 2017 16:02:39 </strong></p>\n<p style=\"white-space: normal;\"><strong>更新说明：</strong></p>\n<p style=\"white-space: normal;\">1. 本次更新更改了图片路径为分享不丢失</p>\n<p style=\"white-space: normal;\">2. 音乐来源是是QQ音乐！</p>\n<p style=\"white-space: normal;\">3. 在小屏幕下隐藏音量隐藏问题</p>\n<p style=\"white-space: normal;\">4. 背景平铺</p>\n<p style=\"white-space: normal;\">5. 昨日2017-5-26上传错误问题。播放无效。已修改</p>\n<p style=\"white-space: normal;\">6. 有群众问外链怎么办，</p>\n<pre class=\"brush:html;toolbar:false\">http://tool.4vtk.com/music/</pre>\n<p style=\"white-space: normal;\">这里可知直接外链，不是临时，直接搜索即可。一共三个音乐站 百度、QQ、酷狗这三个！大家不用上传自己的加载慢的问题了。</p>\n<hr>\n<p style=\"white-space: normal;\"><strong>更新时间：Sat May 27 2017 19:14:54</strong></p>\n<p style=\"white-space: normal;\"><strong>更新说明：</strong></p>\n<p style=\"white-space: normal;\">1. 本次更新更改了图片路径为分享不丢失</p>\n<p style=\"white-space: normal;\">2. 音乐来源是是QQ音乐！</p>\n<p style=\"white-space: normal;\">3. 在小屏幕下隐藏音量隐藏问题</p>\n<p style=\"white-space: normal;\">4. 背景平铺</p>\n<p style=\"white-space: normal;\">5. 昨日2017-5-26上传错误问题。播放无效。已修改</p>                                      \r\n                        <ul class=\"x-top\" style=\"padding-left:0px\">\r\n                            <div class=\"fy f zhs\"><span class=\"jg35\">PREVIOUS:</span></div>\r\n                            <div class=\"fy f txtRight zhs\"><span class=\"jg352\">NEXT:</span></div>\r\n                            <div class=\"fy f\">\n<a class=\"PREVIOUS\" href=\"https://www.jq22.com/jquery-info3468\"> css全屏切换效果源代码 </a> </div>\r\n                            <div class=\"fy f txtRight\">\n<a class=\"NEXT\" href=\"https://www.jq22.com/jquery-info3465\"> jwplayer响应式多终端适配视频播放器 </a> </div>\r\n                            <div class=\"dr\"></div>\r\n                            <div class=\"df\"></div>\r\n                        </ul>', 'https://www.jq22.com/jquery-info3467', NULL, 0, 1, 1771818352, 1771821001, 0.00);

-- ----------------------------
-- Table structure for blog_collect_node
-- ----------------------------
DROP TABLE IF EXISTS `blog_collect_node`;
CREATE TABLE `blog_collect_node`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '节点名称',
  `url` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '采集网址(支持分页参数如[page])',
  `category_id` int NOT NULL DEFAULT 0 COMMENT '入库分类',
  `charset` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT 'utf-8' COMMENT '页面编码',
  `list_selector` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '列表链接选择器(a标签)',
  `title_selector` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '标题选择器',
  `content_selector` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '内容选择器',
  `image_download` tinyint(1) NOT NULL DEFAULT 1 COMMENT '是否下载图片',
  `filter_words` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL,
  `request_delay` int NULL DEFAULT 2,
  `create_time` int NULL DEFAULT NULL,
  `update_time` int NULL DEFAULT NULL,
  `download_selector` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT '' COMMENT '下载地址选择器',
  `price` decimal(10, 2) NULL DEFAULT 0.00 COMMENT '出售金币价格',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '文章采集节点表' ROW_FORMAT = Dynamic;

