  `is_vip_free` tinyint(1) NULL DEFAULT 0 COMMENT 'VIP是否免费',
  `status` tinyint NULL DEFAULT 0 COMMENT '0:Reviewing, 1:Published, 2:Rejected',
  `reject_reason` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '退回原因',
  `create_time` int NOT NULL,
  `update_time` int NOT NULL DEFAULT 0 COMMENT '更新时间',
  `category_id` int NULL DEFAULT 0 COMMENT 'Selected Category ID',
  `price` decimal(10, 2) NULL DEFAULT 0.00 COMMENT 'Requested Selling Price',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `user_id`(`user_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

