  `vip_id` int NULL DEFAULT 0 COMMENT 'VIP等级ID(type=3时使用)',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_orderno`(`order_no` ASC) USING BTREE,
  INDEX `idx_user`(`user_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 125 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '订单表' ROW_FORMAT = Dynamic;

