-- ----------------------------
-- Table structure for blog_vip_level
-- ----------------------------
DROP TABLE IF EXISTS `blog_vip_level`;
CREATE TABLE `blog_vip_level`  (
  `id` int UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '等级名称',
  `level` int NOT NULL DEFAULT 0 COMMENT '等级权重',
  `price` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '价格',
  `duration` int NOT NULL DEFAULT 0 COMMENT '有效天数(0为永久)',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '特权说明',
  `create_time` int NOT NULL DEFAULT 0 COMMENT '创建时间',
  `update_time` int NOT NULL DEFAULT 0 COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = 'VIP等级表' ROW_FORMAT = Dynamic;

